import React from "react";
import { StyleSheet, Text, View, StatusBar, ScrollView, Dimensions, TouchableOpacity } from 'react-native';
import { Ionicons, Entypo, Fontisto } from '@expo/vector-icons';

const StoreListPage = () => {

    const screenWidth = Dimensions.get('window').width;
    const screenHeight = Dimensions.get('window').height;
    const windowHeight = Dimensions.get('screen').height;

    const MiddleCatList = [
        { category: "한식" },
        { category: "분식" },
        { category: "양식" },
        { category: "일식" },
        { category: "중식" },
        { category: "피자" },
        { category: "치킨" },
        { category: "찜/탕" },
    ]

    const StoreList = [
        { storeName: "맛사랑", content: "제육볶음 6,000 / 순두부찌개 6,000", location: "후문", distance: "350m", likes: 33 },
        { storeName: "맛사랑", content: "제육볶음 6,000 / 순두부찌개 6,000", location: "후문", distance: "350m", likes: 33 },
        { storeName: "맛사랑", content: "제육볶음 6,000 / 순두부찌개 6,000", location: "후문", distance: "350m", likes: 33 },
        { storeName: "맛사랑", content: "제육볶음 6,000 / 순두부찌개 6,000", location: "후문", distance: "350m", likes: 33 },
        { storeName: "맛사랑", content: "제육볶음 6,000 / 순두부찌개 6,000", location: "후문", distance: "350m", likes: 33 },
        { storeName: "맛사랑", content: "제육볶음 6,000 / 순두부찌개 6,000", location: "후문", distance: "350m", likes: 33 },
        { storeName: "맛사랑", content: "제육볶음 6,000 / 순두부찌개 6,000", location: "후문", distance: "350m", likes: 33 },
        { storeName: "맛사랑", content: "제육볶음 6,000 / 순두부찌개 6,000", location: "후문", distance: "350m", likes: 33 },
        { storeName: "맛사랑", content: "제육볶음 6,000 / 순두부찌개 6,000", location: "후문", distance: "350m", likes: 33 },
    ]

    return (
        <View style={styles.container}>
            {/* 헤더 */}
            <View style={styles.headerBar}>
                <Ionicons name="arrow-back" size={24} color="gray" />
                <Text style={styles.MiddleCatTitle}>한식</Text>
                <Entypo name="magnifying-glass" size={24} color="gray" />
            </View>

            {/* 분류 카테고리 */}
            <View style={styles.middleCatBar}>
                <ScrollView horizontal showsHorizontalScrollIndicator={false} contentContainerStyle={{ marginTop: 15, flexDirection: 'row' }}>
                    {MiddleCatList.map((item) => (
                        <Text style={styles.middleCatList}>{item.category}</Text>
                    ))}
                </ScrollView>
            </View>

            {/* 가게 리스트 */}
            <View style={styles.storeContainer}>
                <ScrollView>
                    {StoreList.map((item) => (
                        <View style={styles.storeEleWrap}>
                            <View style={styles.storeThumbnail}>
                            </View>
                            <View style={styles.contentWrap}>
                                <Text style={styles.storeName}>{item.storeName}</Text>
                                <Text style={styles.storeContent}>{item.content}</Text>
                                <View style={styles.storeLocationWrap}>
                                    <Text style={styles.location}>{item.location}</Text>
                                    <Entypo name="location-pin" size={12} color="green" />
                                    <Text style={styles.storeDistance}>{item.distance}</Text>
                                    <Ionicons name="ios-heart-circle-outline" size={12} color="red" />
                                    <Text style={styles.storeLikes}>{item.likes}</Text>
                                </View>
                            </View>
                        </View>
                    ))}
                </ScrollView>
            </View>
        </View>
    )
}

export default StoreListPage;

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#fff',
    },
    headerBar: {
        flex: 0.7,
        alignItems: 'center',
        flexDirection: "row",
        justifyContent: "space-between",
        paddingHorizontal: 20
    },
    MiddleCatTitle: {
        fontFamily: 'Bold',
        fontSize: 20
    },
    middleCatBar: {
        flex: 0.5,
        alignContent: 'center',
        borderBottomWidth: 1,
        borderTopWidth: 1,
        borderStyle: 'solid',
        borderColor: '#D8DEDE'
    },
    middleCatList: {
        marginHorizontal: 15,
        fontFamily: 'Regular'
    },
    storeContainer: {
        flex: 8,
    },
    storeEleWrap: {
        flex: 1,
        flexDirection: 'row',
        padding: 15,
        borderBottomColor: 'gray',
        borderStyle: 'solid',
        borderBottomWidth: 0.5,
        alignItems: 'center'
    },
    storeThumbnail: {
        backgroundColor: "#ff9933",
        width: Dimensions.get('window').width * 0.1,
        height: Dimensions.get('window').width * 0.1,
        alignItems: "center",
        justifyContent: 'center',
        borderRadius: Dimensions.get('window').width * 0.1,
        marginRight: 20,
    },
    contentWrap: {
        flexDirection: 'column'
    },
    storeName: {
        fontFamily: 'Medium',
        fontSize: 15
    },
    storeContent: {
        fontFamily: 'Regular',
        fontsize: 10
    },
    storeLocationWrap: {
        flexDirection: "row",
        alignContent: "center",
        alignItems: "center"
    },
    location: {
        marginRight: 5
    },
    storeDistance: {
        marginRight: 5
    }
})
