import React from 'react';
import styled from 'styled-components/native';
import { ScrollView } from 'react-native';

const SC = {
    Container: styled.View`
        align-Content: center;
        border-Bottom-Width: 1px;
        border-Top-Width: 1px;
        border-Color: #D8DEDE;
        padding : 10px 0;
    `,
    category: styled.Text`
        margin : 0 15px;
        font-family: Regular;
    `,
}

const HorizontalBar = (props) => {
    const catList = props.catList;
    return (
        <SC.Container>
            <ScrollView horizontal showsHorizontalScrollIndicator={false} contentContainerStyle={{ flexDirection: 'row', alignItems: 'center' }}>
                {catList.map((item) => (
                    <SC.category>{item.category}</SC.category>
                ))}
            </ScrollView>
        </SC.Container>
    )
}

export default HorizontalBar
