import React from 'react';
import { StyleSheet, Text, View, TouchableOpacity, Dimensions } from 'react-native';
import { ScrollView } from 'react-native-gesture-handler';
import { Foundation, FontAwesome } from '@expo/vector-icons';
import { Rating } from 'react-native-ratings';
import styled from 'styled-components/native';

const StoreReviewTab = () => {
    const reviewList = [
        {
            nickname: "효다몬",
            content: "ㄹㅇ 맛있음",
            score: 5,
            date: "2021-09-12"
        },
        {
            nickname: "코딩하는 노예",
            content: "자주 먹으니깐 질림",
            score: 4,
            date: "2021-09-10"
        },
        {
            nickname: "이상민",
            content: "양이 너무 많음",
            score: 5,
            date: "2021-09-09"
        },
        {
            nickname: "정유진",
            content: "",
            score: 4,
            date: "2021-09-02"
        },
        {
            nickname: "아무개",
            content: "",
            score: 5,
            date: "2021-09-01"
        },
        {
            nickname: "홍길동",
            content: "",
            score: 5,
            date: "2021-08-12"
        },
        {
            nickname: "혜팔이",
            content: "야야야야야야야야",
            score: 1,
            date: "2021-08-12"
        },
    ]

    let scoreSum = 0
    const scoreDist = [0, 0, 0, 0, 0] // 별점 분포
    reviewList.map((item) => {
        scoreSum += item.score
        scoreDist[5 - item.score] += 1
    })

    const scoreAvg = (scoreSum / reviewList.length).toFixed(1); // 평균 별점

    const SC = {
        scoreFilledBar: styled.View`
            width : ${props => (props.gauge / scoreDist.reduce((a, b) => a + b)) * 30}vw;
            height : 8px;
            background-color: #ff9933;
            margin-right: 10;
            border-radius: 15vw;
            position: absolute;
            left : 31.25px;
        `,
        scoreEmptyBar: styled.View`
            width : 30vw;
            height : 8px;
            background-color : #D3D3D3;
            margin-right: 10;
            border-radius: 15vw;
        `,
    };

    return (
        <View style={styles.container}>
            <ScrollView>
                <View style={styles.scoreContainer}>
                    <View style={styles.scoreWrap}>
                        <Text style={styles.scoreAvg}>{scoreAvg}</Text>
                        <Rating
                            readonly={true}
                            imageSize={18}
                            startingValue={scoreAvg}
                        />
                    </View>
                    <View style={styles.scoresSummaryWrap}>
                        {scoreDist.map((item, index) => (
                            <View style={styles.oneScoreSummary}>
                                <Text style={styles.scoreNumber}>{5 - index}점</Text>
                                <SC.scoreEmptyBar></SC.scoreEmptyBar>
                                <SC.scoreFilledBar gauge={item}></SC.scoreFilledBar>
                                <Text style={styles.totalScore}>{item}</Text>
                            </View>
                        ))}
                    </View>
                </View>
                <View style={styles.optionBar}>
                    <View style={styles.reviewsSummaryWrap}>
                        <Text style={styles.reviewText}>리뷰</Text>
                        <Text style={styles.reviewNumber}>{reviewList.length}개</Text>
                    </View>
                    <View style={styles.reviewViewOption}>

                    </View>
                </View>
                <View style={styles.reviewContainer}>
                    {reviewList.map((item) => (
                        <View style={styles.reviewWrap}>
                            <View style={styles.reviewLeft}>
                                <Text style={styles.writerName}>{item.nickname}</Text>
                                <Rating
                                    readonly={true}
                                    imageSize={12}
                                    startingValue={item.score}
                                />
                                <Text style={styles.reviewContent}>{item.content}</Text>
                            </View>
                            <Text style={styles.reviewDate}>{item.date}</Text>
                        </View>
                    ))}
                </View>

            </ScrollView >
            <View style={styles.reviewWriteWrap}>
                <TouchableOpacity stlye={styles.reviewWriteBtn}>
                    <Foundation name="pencil" size={24} color="white" />
                </TouchableOpacity>
            </View>
        </View >
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#FFFFFF',
    },
    /* 별점 요약 컨테이너 */
    scoreContainer: {
        flex: 2,
        flexDirection: 'row',
        justifyContent: 'space-between',
        alignItems: 'center',
        paddingHorizontal: 40,
        borderStyle: 'solid',
        borderBottomWidth: 1,
        borderBottomColor: '#d3d3d3',
    },
    scoreAvg: {
        fontFamily: 'Bold',
        fontSize: 42
    },
    oneScoreSummary: {
        flexDirection: 'row',
        alignItems: 'center',
        justifyContent: 'space-between',
    },
    scoreNumber: {
        fontFamily: 'Regular',
        color: '#808080',
        marginRight: 10
    },
    totalScore: {
        fontFamily: 'Light',
        color: '#c0c0c0'
    },
    /* 중간 바 */
    optionBar: {
        flex: 0.5,
        flexDirection: 'row',
        justifyContent: 'space-between',
        alignItems: 'center',
        paddingHorizontal: 20,
        borderStyle: 'solid',
        borderBottomWidth: 1,
        borderBottomColor: '#d3d3d3',
    },
    reviewsSummaryWrap: {
        flexDirection: 'row'
    },
    reviewText: {
        fontFamily: 'Light',
        color: 'gray'
    },
    reviewNumber: {
        marginLeft: 5,
        fontFamily: 'Regular'
    },
    /* 리뷰 */
    reviewContainer: {
        flex: 7
    },
    reviewWrap: {
        flexDirection: 'row',
        justifyContent: 'space-between',
        padding: 20,
        borderStyle: 'solid',
        borderBottomWidth: 1,
        borderBottomColor: 'gray',
    },
    writerName: {
        fontFamily: 'Medium',
        fontSize: 16
    },
    reviewContent: {
        marginTop: 10,
        fontFamily: 'Regular',
        fontSize: 14
    },
    reviewDate: {
        fontFamily: 'Light',
        fontSize: 10,
        color: 'gray'
    },
    reviewWriteWrap: {
        position: 'absolute',
        bottom: 0,
        alignSelf: 'flex-end',
        margin: 25,
        backgroundColor: "#ff9933",
        width: Dimensions.get('window').width * 0.15,
        height: Dimensions.get('window').width * 0.15,
        alignItems: 'center',
        justifyContent: 'center',
        borderRadius: Dimensions.get('window').width * 0.15
    },
    reviewLeft: {
        alignItems: 'flex-start'
    },
    scoreWrap: {
        alignItems: 'center'
    }
    ,
    star: {
        color: '#EDDA05'
    }
});

export default StoreReviewTab;
